import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:80/reservations",
});

const api2 = axios.create({
  baseURL: "http://localhost:80/halls",
});

export const getAllReservations = () => api.get("/");
export const getReservationById = (id) => api.get("/${id}");
export const createReservation = () => api.post("/");
export const deleteReservation = () => api.delete("/${id}");

const apis = {
  getAllReservations,
  getReservationById,
};

export default apis;
