import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:80/reservations",
});

const api2 = axios.create({
  baseURL: "http://localhost:80/halls",
});

//reservation api
export const getAllReservations = () => api.get("/");
export const getReservationById = (id) => api.get("/${id}");
export const createReservation = () => api.post("/");
export const deleteReservation = () => api.delete("/${id}");

//halls api
export const getAllHalls = () => api2.get("/");
export const getHallsByTime = () => api2.get("/${time}");

const apis = {
  getAllReservations,
  getReservationById,
  createReservation,
  deleteReservation,
  getAllHalls,
  getHallsByTime,
};

export default apis;
