import "./PersonalInfo.css";
import { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import Paper from "@mui/material/Paper";
import api from "./api";

export default function PersonalInfo() {
  const data = api.getAllReservations().then((response) => {
    console.log(response.data);
  });

  const data2 = api.getAllHalls().then((response) => {
    console.log(response.data);
  });
  //name, phone, title, date, time, seats

  const [title, setTitle] = useState("듄");
  const [time, setTime] = useState("1230");
  const [seat, setSeat] = useState(["F11", "F12"]);
  const [birthday, setBirthday] = useState("");
  const [phone, setPhone] = useState("");
  const [pwd, setPwd] = useState("");
  const [pwdConfirm, setPwdConfirm] = useState("");
  const [done, setDone] = useState(false);
  const [open, setOpen] = useState(true);

  const handleChange = (event) => {
    switch (event.target.id) {
      case "birthday":
        setBirthday(event.target.value);
        break;
      case "phone":
        setPhone(event.target.value);
        break;
      case "pwd":
        setPwd(event.target.value);

        break;
      case "pwdConfirm":
        setPwdConfirm(event.target.value);
        break;
      default:
        return;
    }
  };

  const handleConfirm = () => {
    if (pwd === pwdConfirm) {
      console.log("예약이 완료되었습니다.");
      setDone(true);
    } else {
      console.log("비밀번호가 일치하지 않습니다.");
    }
  };

  const parseTime = (time) => {
    const temp = time.slice(0, 2) + ":" + time.slice(2);
    return temp;
  };

  return done ? (
    <div>
      <p>{phone}님의 예약이 완료되었습니다.</p>
      <p>시간: {parseTime(time)}</p>
      <p>좌석: {seat.join(", ")}</p>
      <Button
        style={{
          margin: "2vh 0",
          backgroundColor: "lightGray",
          fontSize: "16px",
          width: "6vw",
        }}
      >
        돌아가기
      </Button>
    </div>
  ) : (
    <div>
      <Modal open={open}>
        <div id="modalRoot">
          <div id="firstHalf">
            <div id="title">{title}</div>

            <div id="dateTime">
              <p> 2021.11.03(수)</p>
              <p> {parseTime(time)}</p>
            </div>
            <div id="seatPrice">
              <p>{seat.join(", ")}</p>
              <p>198,000원</p>
            </div>
          </div>
          <hr class="solid" />

          <div id="bottomHalf">
            <div id="enterInfo">
              <div className="textfield">
                <p className="subtitle">생년월일(6자리)</p>
                <TextField
                  id="birthday"
                  value={birthday}
                  onChange={handleChange}
                  inputProps={{ maxLength: 11 }}
                  InputProps={{
                    style: { fontSize: "40px" },
                  }}
                  variant="standard"
                  style={{ width: "60%" }}
                />
              </div>
              <div className="textfield">
                <p className="subtitle">휴대폰 번호</p>
                <TextField
                  id="phone"
                  value={phone}
                  onChange={handleChange}
                  inputProps={{ maxLength: 11 }}
                  size="small"
                />
              </div>
              <div className="textfield">
                <p className="subtitle">비밀번호(4자리)</p>
                <TextField
                  id="pwd"
                  type="password"
                  value={pwd}
                  onChange={handleChange}
                  inputProps={{ maxLength: 4 }}
                  size="small"
                />
              </div>
              <div className="textfield">
                <p className="subtitle">비밀번호 확인</p>
                <TextField
                  id="pwdConfirm"
                  type="password"
                  value={pwdConfirm}
                  onChange={handleChange}
                  inputProps={{ maxLength: 4 }}
                  size="small"
                />
              </div>
            </div>
          </div>

          <div id="buttons">
            <Button
              disabled={
                phone.length < 10 || pwd.length !== 4 || pwdConfirm.length !== 4
              }
              onClick={handleConfirm}
              color="info"
              style={{
                backgroundColor: "lightGray",
                margin: "0 2vw",
                fontSize: "16px",
                width: "6vw",
              }}
            >
              확인
            </Button>
            <Button
              style={{
                backgroundColor: "lightGray",
                margin: "0 2vw",
                fontSize: "16px",
                width: "6vw",
              }}
            >
              돌아가기
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
}
